package GeneralPrograms;

public class primeNum {
	
	static void isPrime(int n) {		
			if(n%2==0) {
				System.out.println("not a prime");
				
			}else {
				System.out.println("is a prime");			
		}	
}

	public static void main(String[] args) {
		// TODO Auto-generated method stub		
		isPrime(17);
	}

}
